mapper.select:
select a.*,b.ParComNameC,b.ParSingleName
from AsmInHead a left join Custom b on a.Customcode = b.ParComID


        b.ParSingleName LIKE "%${params.customerName}%"


        AND a.AsmDT >= #{params.beginDate}


        AND a.AsmDT <= #{params.endDate}

select a.* from AsmOutHead a


        a.AsmDT >= #{params.beginDate}


        AND a.AsmDT <= #{params.endDate}
