mapper.select:
select a.*,b.ParComNameC,b.ParSingleName,c.Name from MatInHead a LEFT JOIN Custom b on a.SupplyerCode = b.ParComID LEFT JOIN Store c on a.StoreCode = c.code
b.ParSingleName LIKE '%${params.customerName}%'
AND a.MatinDT >= #{params.beginDate}
AND a.MatinDT <= #{params.endDate}

SELECT a.*,b.Name FROM MatOutHead a LEFT JOIN Store b ON a.DeptCode = b.Code
b.Name LIKE '%${params.customerName}%'
AND a.OutDT >= #{params.beginDate}
AND a.OutDT <= #{params.endDate}

SELECT a.*,b.prodNameC FROM MatMonRpt a LEFT JOIN Materical b ON a.ProdCode = b.prodcode
a.MatYear = #{params.matYear} and a.MatMonth = #{params.matMonth}
and b.prodNameC LIKE '%${params.prodNameC}%'
