mapper.select:
select a.*,b.ParComNameC,b.ParSingleName,c.Name
from MatInHead a
LEFT JOIN Custom b on a.SupplyerCode = b.ParComID
LEFT JOIN Store c on a.StoreCode = c.code


        b.ParSingleName = #{params.customerName}


        AND a.CreateDT >= #{params.beginDate}


        AND a.CreateDT <= #{params.endDate}

select a.* from AsmOutHead a


        a.AsmDT >= #{params.beginDate}


        AND a.AsmDT <= #{params.endDate}

SELECT b.*, a.StockPieces, a.StockQuan, c.ParSingleName FROM ( SELECT AsmNo, SUM(StockPieces) StockPieces, ROUND(SUM(StockQuan), 2) StockQuan FROM AsmSave WHERE StockQuan > 0 GROUP BY AsmNo ) a LEFT JOIN AsmInHead b ON a.AsmNo = b.AsmNO LEFT JOIN Custom c on b.Customcode = c.ParComID
b.AsmDT >= #{params.beginDate}
AND b.AsmDT <= #{params.endDate}
