mapper.select:
SELECT a.ManuCrock, b.ParSingleName, a.CreateOrderDT, a.CurrProcCode, a.ProcedureCode, c.NAME, e.ProdNameC, d.SName, a.Pieces, a.SubQuan FROM WOHead a LEFT JOIN Custom b ON a.CustCode = b.ParComID LEFT JOIN ManuProcess c ON a.CurrProcCode = c.CODE LEFT JOIN ProdColor d ON a.ColorCode = d.SCode LEFT JOIN Product e ON a.ProdCode = e.ProdCode
b.ParSingleName = #{params.customerName}
AND a.CreateOrderDT >= #{params.beginDate}
AND a.CreateOrderDT <= #{params.endDate}
AND a.manuCrock LIKE '%${params.manuCrock}%'
AND b.ParComID = #{params.ParComID}

SELECT a.*,b.ManuCrock,e.ProdNameC,d.SName,c.Pieces,c.SubQuan FROM DrawFabHead a LEFT JOIN DrawFabDetail b ON a.DrawFabNO = b.DrawFabNO LEFT JOIN WOHead c ON b.ManuCrock = c.ManuCrock LEFT JOIN ProdColor d ON c.ColorCode = d.SCode LEFT JOIN Product e ON c.ProdCode = e.ProdCode
a.DrawDT >= #{params.beginDate}
AND a.DrawDT <= #{params.endDate}
AND b.ManuCrock LIKE '%${params.manuCrock}%'
